"use strict";var email=document.getElementById("input_email");var warning=document.getElementById("email_warning");var user_id=document.getElementById("user_id");function validateEmail(){var xhr=new XMLHttpRequest;xhr.open("GET","/account/validate-email?email="+email.value+"&user_id="+(user_id?user_id.value:null),true);xhr.onload=function(){if(xhr.status===200){warning.innerText=JSON.parse(xhr.responseText)?"Email already taken!":""}};xhr.send()}email.addEventListener("focus",validateEmail,true);email.addEventListener("change",validateEmail);email.addEventListener("blur",validateEmail);